import React from "react";
import { Form, FormGroup, Row, Col, Container } from 'reactstrap';
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import MenuSuperior from "../menuSuperior/MenuSuperior";

import { TextField } from "@material-ui/core";
import { withStyles, makeStyles } from '@material-ui/core/styles';




const CssTextField = withStyles({
    root: {
        "& label.Mui-focused": {
            color: "black"
        },
        "& label": {
            color: "black"
        },
        "& .MuiInput-underline:before": {
            borderBottomColor: "black"
        },
        "& .MuiInput-underline:after": {
            borderBottomColor: "black"
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: "black"
            },
            "&:hover fieldset": {
                borderColor: "black"
            },
            "&.Mui-focused fieldset": {
                borderColor: "black"
            }
        }
    }
})(TextField);


const checkpromo = (props) => {
    return (
        <div>
            <MenuSuperior />
            <div>
                <Container className="container-theme" fluid={true}>
                    <Form>
                        <p>Códigos</p>
                        <Row>
                            <Col sm={12} md={4} lg={4} xl={4}>
                                <FormGroup>
                                    <TextField
                                        id="standard-multiline-flexible"
                                        label="De:"
                                        multiline
                                        maxRows={4}
                                        value=""
                                    />
                                </FormGroup>
                            </Col>
                            <Col sm={12} md={4} lg={4} xl={4}>
                                <FormGroup>
                                    <TextField
                                        id="standard-multiline-flexible"
                                        label="Até:"
                                        multiline
                                        maxRows={4}
                                        value=""
                                    />
                                </FormGroup>
                            </Col>
                        </Row>
                    </Form>
                    <Form>
                        <Row>
                            <Col sm={12} md={4} lg={4} xl={4} >
                                <FormGroup >
                                    <InputLabel id="select-label"></InputLabel>
                                    <Select
                                        labelId="select-label"
                                        id="select"
                                        label=""
                                    >
                                        <MenuItem selected disabled value="">Selecione Opção</MenuItem>
                                        <MenuItem value="1">Modinha</MenuItem>
                                        <MenuItem value="2">Plus Size</MenuItem>
                                        <MenuItem value="10">Infantil</MenuItem>
                                    </Select>
                                </FormGroup>

                            </Col>
                        </Row>
                    </Form>
                </Container>
            </div>
        </div>
    );
}
export default checkpromo;
