import React from "react";
import { Form, FormGroup, Row, Col, Container } from 'reactstrap';
import { FormControl, InputLabel, Select, MenuItem, Unstable_TrapFocus } from "@material-ui/core";
import MenuSuperior from "../menuSuperior/MenuSuperior";
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { TextField, Button } from "@material-ui/core";
import { withStyles, makeStyles, lighten } from '@material-ui/core/styles';

import { MDBInput, MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import Grid from "antd/lib/card/Grid";



const data_checkboxes = {
    columns: [
        {
            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'field': 'check',
            'sort': 'asc'

        },
        {
            'label': 'DESCRIÇÃO',
            'field': 'lorem',
            'sort': 'asc'
        },
    ],
    rows: [
        {
            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'Descrição': ' ',

        },
    ]
};

const CssTextField = withStyles({
    root: {
        "& label.Mui-focused": {
            color: "black"
        },
        "& label": {
            color: "black"
        },
        "& .MuiInput-underline:before": {
            borderBottomColor: "black"
        },
        "& .MuiInput-underline:after": {
            borderBottomColor: "black"
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: "black"
            },
            "&:hover fieldset": {
                borderColor: "black"
            },
            "&.Mui-focused fieldset": {
                borderColor: "black"
            }
        }
    }
})(TextField);



const checkpromo = (props) => {
    return (
        <div>
            <MenuSuperior />
            <div >
                <Col xl={6}>
                    <Container className="container-theme" fluid={true}>
                        <Form bordered style={{ margin: '25% 0 0 0' }}>
                            <Row>
                                <Col sm={12} md={4} lg={4} xl={4}>
                                    <FormGroup >
                                        <TextField
                                            className="campo"
                                            id="standard-multiline-flexible"
                                            label="De:"
                                            multiline
                                            maxRows={4}
                                            value=""
                                        />
                                    </FormGroup>
                                </Col>
                                <Col sm={12} md={4} lg={4} xl={4}>
                                    <FormGroup>
                                        <TextField
                                            className="campo"
                                            id="standard-multiline-flexible"
                                            label="Até:"
                                            multiline
                                            maxRows={4}
                                            value=""
                                        />
                                    </FormGroup>
                                </Col>
                            </Row>
                        </Form>
                        <Form id="altura">
                            <Row>
                                <Col sm={12} md={4} lg={4} xl={4} >
                                    <FormGroup >
                                        <InputLabel id="select-label">Buscar</InputLabel>
                                        <Select
                                            labelId="select-label"
                                            id="select"
                                            label="Buscar"
                                        >
                                            <MenuItem selected disabled value="">Selecione Opção</MenuItem>
                                            <MenuItem value=""></MenuItem>
                                            <MenuItem value=""></MenuItem>
                                            <MenuItem value=""></MenuItem>
                                        </Select>
                                    </FormGroup>
                                </Col>
                                <Col sm={12}>
                                    <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width }}>Filtrar</Button>{' '}
                                </Col>
                            </Row>
                        </Form>
                    </Container>
                    <Container>
                        <Row>
                            <Form id="altura">

                                <Col >
                                    <Grid item xs={6}>

                                        <MDBTable bordered style={{ margin: '-90% 0 0 400%' }}>
                                            <MDBTableHead columns={data_checkboxes.columns} />
                                            <MDBTableBody rows={data_checkboxes.rows} />
                                        </MDBTable>
                                    </Grid>
                                </Col>
                            </Form>
                        </Row>
                    </Container>
                </Col>
                <Row>
                    <Col sm={12}   bordered style={{ margin: '10% 0 0 2%' }} >
                       
                        <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width:"10%" }}>Enviar</Button>{' '}
                    </Col>
                </Row>
            </div>

        </div>
    );
}
export default checkpromo;
