import React, { Component} from "react";
import "./TabelaFornecedor.css";
import axios from 'axios';
//import { Table, CardTitle, Card, Label, Row, Button, Col } from 'reactstrap';
//import DatePicker from "react-date-picker";


export default class TabelaFornecedor extends Component {
  constructor(props) {
      super(props);
      this.state = { listaItens: [] }
  
      this.refresh = this.refresh.bind(this);
  }
  
  refresh(Nome = '') {
      const search = Nome ? `&Nome__regex=/${Nome}/` : ''
      axios.get(`${URL}?sort=-createdAt${search}`)
      .then(response => { this.setState({ listaItens: response.data}); })
      .catch(() => { console.log('Erro ao recuperar os dados'); });    
  }
  
  componentDidMount() {
      this.refresh()
  }
  
  render() {
      return (
        <div>
        <table>

            <tr>
                <th scope="col">Nome</th>
                <th scope="col">CPF/CNPJ</th>
            </tr>

           {this.state.listaItens.map(function(item)  
              {
                 return (
                     <tr>
                         <td>{item.Nome}</td>
                         <td>{item.CpfCnpj}</td>
                     </tr>
                 )
              }
           )}

        </table>
    </div>
) 
}}
