import React, { useState, useCallback, useEffect } from "react";
import { Form, FormGroup, Row, Col, Container } from 'reactstrap';
import { InputLabel, Select, MenuItem } from "@material-ui/core";
import MenuSuperior from "../menuSuperior/MenuSuperior";
import api from '../../api'

import { TextField, Button } from "@material-ui/core";
import { withStyles } from '@material-ui/core/styles';

import { MDBInput, MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import Grid from "antd/lib/card/Grid";
import Card from "reactstrap/lib/Card";




const data_checkboxes = {
    columns: [
        {
            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'field': 'check',
            'sort': 'asc'

        },
        {
            'label': 'Código',
            'field': 'lorem',
            'sort': 'asc'
        },
        {
            'label': 'Descrição',
            'field': 'lorem',
            'sort': 'asc',
        },
    ],
    rows: [
        {

            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'Descrição': ' ',

        },
    ]
};

const CssTextField = withStyles({
    root: {
        "& label.Mui-focused": {
            color: "black"
        },
        "& label": {
            color: "black"
        },
        "& .MuiInput-underline:before": {
            borderBottomColor: "black"
        },
        "& .MuiInput-underline:after": {
            borderBottomColor: "black"
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: "black"
            },
            "&:hover fieldset": {
                borderColor: "black"
            },
            "&.Mui-focused fieldset": {
                borderColor: "black"
            }
        }
    }
})(TextField);



const checkpromo = (props) => {
    const [combocodigo, setCombocodigo] = useState([]);

    useEffect(() => {
        api.get(`combocodigo/${for_cod}/`).then(response => {
            setCombocodigo(response.data)
        })
    }, [for_cod]);

    return (
        <div>
            <MenuSuperior />

            <center><h1 style={{ marginBottom: '1%', fontFamily: "Roboto, sans-serif", color: '#A9A9A9' }}>Produtos de Promoção</h1></center>
            <Container className="container-theme" fluid={true}>
                <Row>
                    <Col lg={8}>

                        <Card style={{ width: "30%", height: "80%", boxshadow: '#848484' }}>
                            <Form id="altura">
                                <Row>
                                    <Col sm={12} md={4} lg={4} xl={4} >
                                        <FormGroup >
                                            <InputLabel id="select-label"></InputLabel>
                                            <Select
                                                labelId="select-label"
                                                id="select"
                                                label="Buscar"
                                                style={{ width: "450%", margin: '0 0 0 10%' }}

                                            >
                                                <option value='0'>SELECIONE OPÇÃO</option>
                                                {combocodigo.map(combocodigo => (

                                                    <option key={combocodigo.for_cod} value={combocodigo.for_cod} > {combocodigo.for_cod + ' - ' + combocodigo.for_nom}  </option>

                                                ))}
                                            </Select>
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col sm={12}>
                                        <FormGroup>
                                            <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width: "40%", height: "2%", margin: '0% 0 0 5%', fontSize: "13px" }}>Filtrar</Button>{' '}
                                        </FormGroup>
                                    </Col>
                                </Row>
                            </Form>
                        </Card>

                    </Col>
                    <Col lg={4}>

                        <Row>
                            <Form id="altura">

                                <Grid>
                                    <MDBTable bordered style={{ margin: '0 0 0 -200%' }}>
                                        <MDBTableHead columns={data_checkboxes.columns} />
                                        <MDBTableBody rows={data_checkboxes.rows} />
                                    </MDBTable>
                                </Grid>
                                <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width: "50%", height: "15%", margin: '10% 0 0 -200%', fontSize: "13px" }}>Enviar</Button>{' '}
                            </Form>
                        </Row>
                    </Col>
                </Row>
            </Container>

        </div>
    );
}
export default checkpromo;
