import React from "react";
import { Form, FormGroup, Row, Col, Container } from 'reactstrap';
import { FormControl, InputLabel, Select, MenuItem, Unstable_TrapFocus } from "@material-ui/core";
import MenuSuperior from "../menuSuperior/MenuSuperior";
import PropTypes from 'prop-types';
import clsx from 'clsx';
import { TextField, Button } from "@material-ui/core";
import { withStyles, makeStyles, lighten } from '@material-ui/core/styles';

import { MDBInput, MDBTable, MDBTableBody, MDBTableHead } from 'mdbreact';
import Grid from "antd/lib/card/Grid";
import Card from "reactstrap/lib/Card";




const data_checkboxes = {
    columns: [
        {
            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'field': 'check',
            'sort': 'asc'

        },
        {
            'label': 'coluna1',
            'field': 'lorem',
            'sort': 'asc'
        },
        {
            'label': 'coluna2',
            'field': 'lorem',
            'sort': 'asc',
        },
    ],
    rows: [
        {

            'check': <MDBInput type="checkbox" id="checkbox2" />,
            'Descrição': ' ',

        },
    ]
};

const CssTextField = withStyles({
    root: {
        "& label.Mui-focused": {
            color: "black"
        },
        "& label": {
            color: "black"
        },
        "& .MuiInput-underline:before": {
            borderBottomColor: "black"
        },
        "& .MuiInput-underline:after": {
            borderBottomColor: "black"
        },
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: "black"
            },
            "&:hover fieldset": {
                borderColor: "black"
            },
            "&.Mui-focused fieldset": {
                borderColor: "black"
            }
        }
    }
})(TextField);



const checkpromo = (props) => {
    return (
        <div>
            <MenuSuperior />
            <div >
                <Col xl={6}>
                    <Container className="container-theme" fluid={true}>
                        <Card style={{margin:'20% 0 0 0', width:"50%", height:"60%"}}>
                            
                            <Form>

                                <Row>
                                    <Col sm={12} md={4} lg={4} xl={4}>
                                        <FormGroup >
                                            <TextField
                                                className="campo"
                                                id="standard-multiline-flexible"
                                                label="De:"
                                                multiline
                                                maxRows={4}
                                                value=""
                                                style={{ margin: '20% 0 0 10%', width:"190%"}}
                                            />
                                        </FormGroup>
                                    </Col>
                                    <Col sm={12} md={4} lg={4} xl={4}>
                                        <FormGroup>
                                            <TextField
                                                className="campo"
                                                id="standard-multiline-flexible"
                                                label="Até:"
                                                multiline
                                                maxRows={4}
                                                value=""
                                                style={{ margin: '20% 0 0 90%',width:"190%"}}
                                            />
                                        </FormGroup>
                                    </Col>
                                </Row>

                            </Form>
                            <Form id="altura">
                                <Row>
                                    <Col sm={12} md={4} lg={4} xl={4} >
                                        <FormGroup >
                                            <InputLabel id="select-label"></InputLabel>
                                            <Select
                                                labelId="select-label"
                                                id="select"
                                                label="Buscar"
                                                style={{ width: "200%", margin:'0 0 0 10%' }}

                                            >
                                                <MenuItem selected disabled value="">Selecione Opção</MenuItem>
                                                <MenuItem value=""></MenuItem>
                                                <MenuItem value=""></MenuItem>
                                                <MenuItem value=""></MenuItem>
                                            </Select>
                                        </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col sm={12}>
                                        <FormGroup>
                                            <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width: "25%", height: "2%", margin: '0% 0 0 5%', fontSize: "13px" }}>Filtrar</Button>{' '}
                                            <Button style={{ background: "#007bff", color: "#fff", borderRadius: "0%", width: "25%", height: "0.5%", margin: '0.5% 0 0 8%', fontSize: "13px" }}>Enviar</Button>{' '}
                                        </FormGroup>
                                    </Col>
                                </Row>

                            </Form>
                        </Card>
                    </Container>
                    <Container>
                        <Row>
                            <Form id="altura">

                                <Col >

                                    <Grid item xs={6}>

                                        <MDBTable bordered style={{ margin: '-70% 0 0 300%' }}>
                                            <MDBTableHead columns={data_checkboxes.columns} />
                                            <MDBTableBody rows={data_checkboxes.rows} />
                                        </MDBTable>
                                    </Grid>
                                </Col>
                            </Form>
                        </Row>
                    </Container>
                </Col>

            </div>

        </div>
    );
}
export default checkpromo;
